<script>

    export let data, columns, order, dir;

    export let classTableWrapper = "bench-wrapper";
    export let classTable = "bench-table";
    export let classTableBody = "bench-tbody";
    export let classTableHead = "bench-thead";
    export let classTableData = "bench-td";
    export let classTableHeader = "bench-th";
    export let classTableRow = "bench-tr";

    const sort = (col) => {
        if (col.sort !== false) {
            if (col.id == order) {
                if (!dir) {
                    dir = 'desc';
                } else {
                    dir = (dir == 'desc') ? 'asc' : 'desc';
                }
            } else {
                order = col.id;
                dir = 'desc';
            }
        }
    }

</script>

<bench-table>
    {#if data.results}
    <div class={classTableWrapper}>
        <table class={classTable}>
            <thead class={classTableHead}>
                <tr class={classTableRow}>
                    {#each columns as col}
                    <th class="{classTableHeader} {col.sort !== false ? 'sort' : ''} {col.id == order ? 'sorted' : ''}"
                        style={col.hidden === true ? 'display: none' : ''} on:click={() => sort(col)}
                    >
                        {col.name ?? ""}
                        {#if col.sort !== false}
                            <span class="sortIcon">
                                {#if col.id == order}
                                <button class="{(dir == 'desc') ? "sort-desc" : "sort-asc"}" />
                                {:else}
                                <button class="unsorted" />
                                {/if}
                            </span>
                        {/if}
                    </th>
                    {/each}
                </tr>
            </thead>
            <tbody class={classTableBody}>
                {#each data.results as row}
                <tr class={classTableRow}>
                    {#each columns as col}
                    <td class={classTableData}
                        style={col.hidden === true ? 'display: none' : ''}
                        on:click={e => col.onClick ? col.onClick(e) : undefined }
                        on:keydown={e => col.onKeyDown ? col.onKeyDown(e) : undefined }
                        on:change={e => col.onChange ? col.onChange(e) : undefined }
                        on:mouseover={e => col.onMouseOver ? col.onMouseOver(e) : undefined }
                        on:focus={e => col.onFocus ? col.onFocus(e) : undefined }
                        on:mouseout={e => col.onMouseOut ? col.onMouseOut(e) : undefined }
                        on:blur={e => col.onBlur ? col.onBlur(e) : undefined }
                        on:keydown={e => col.onKeyDown ? col.onKeyDown(e) : undefined }
                    >
                        {#if col.formatter}
                            {#if col.html}
                                {@html col.formatter(row[col.id]) ?? ""}
                            {:else}
                                {col.formatter(row[col.id]) ?? ""}
                            {/if}
                        {:else}
                            {#if col.html}
                                {@html row[col.id] ?? ""}
                            {:else}
                                {row[col.id] ?? ""}
                            {/if}
                        {/if}
                    </td>
                    {/each}
                </tr>
                {/each}
            </tbody>
        </table>
    </div>
    {/if}
</bench-table>

<style>
    bench-table {
        margin-top: 10px;
        grid-area: bench-table;
    }

    .bench-wrapper {
        -webkit-font-smoothing: antialiased;
        -moz-osx-font-smoothing: grayscale;
        border-color: #e5e7eb;
        border-radius: 8px 8px 0 0;
        border-top-width: 1px;
        box-shadow: 0 1px 3px #0000001a, 0 1px 2px #00000042;
        display: block;
        overflow: auto;
        position: relative;
        width: 100%;
        z-index: 1;
    }

    table.bench-table {
        border-collapse: collapse;
        display: table;
        margin: 0;
        max-width: 100%;
        overflow: auto;
        padding: 0;
        table-layout: auto;
        text-align: left;
        width: 100%;
    }

    td.bench-td:first-child {
        border-left: none;
    }

    td.bench-td:last-child {
        border-right: none;
    }

    td.bench-td {
        border: 1px solid #e5e7eb;
        box-sizing: content-box;
        padding: 0.5rem 0.5rem;
        overflow: auto;
    }

    .bench-tbody, td.bench-td {
        background-color: #fff;
    }

    th.bench-th {
        background-color: #f9fafb;
        border: 1px solid #e5e7eb;
        border-top: none;
        box-sizing: border-box;
        color: #6b7280;
        outline: none;
        padding: 0.2rem 0.5rem;
        position: relative;
        -webkit-user-select: none;
        -moz-user-select: none;
        user-select: none;
        vertical-align: middle;
        white-space: nowrap;
        font-weight: normal;
    }

    th.sort {
        cursor: pointer;
    }

    th.sorted {
        font-weight: bold;
    }

    th.bench-th span.sortIcon button {
        border: none;
        cursor: pointer;
        float: right;
        height: 20px;
        margin: 0;
        outline: none;
        padding: 0;
        width: 13px;
    }

    th.bench-th span.sortIcon button.unsorted {
        mask: url(data:image/svg+xml;base64,);
        mask-size: contain;
        mask-position: center;
        mask-repeat: no-repeat;
        background-color: #6b7280;
    }

    th.bench-th span.sortIcon button.sort-asc {
        mask: url(data:image/svg+xml;base64,);
        mask-size: contain;
        mask-position: center;
        mask-repeat: no-repeat;
        background-color: #6b7280;
        mask-position: top;
        margin-top: 2px;
        width: 10px;
    }

    th.bench-th span.sortIcon button.sort-desc {
        mask: url(data:image/svg+xml;base64,);
        mask-size: contain;
        mask-position: center;
        mask-repeat: no-repeat;
        background-color: #6b7280;
        mask-position: bottom;
        margin-bottom: 2px;
        width: 10px;
    }

</style>
